mapper.select:
SELECT p.payment_id, p.reservation_id, u.nickname AS userNickname, z.zone_name AS campground_name, p.payment_price, p.payment_method, p.payment_status, r.refund_status, p.paid_at AS paid_at FROM payments p JOIN reservations r ON p.reservation_id = r.reservation_id JOIN users u ON r.provider_code = u.provider_code AND r.provider_user_id = u.provider_user_id JOIN campground_sites s ON r.reservation_site = s.site_id JOIN campground_zones z ON s.zone_id = z.zone_id
AND r.reservation_status = #{reservationStatus}
AND p.payment_status = #{paymentStatus}
AND r.refund_status = #{approvalStatus}
AND DATE(p.paid_at) >= #{startDate}
AND DATE(p.paid_at) <= #{endDate}
AND ( u.nickname LIKE CONCAT('%', #{keyword}, '%') OR z.zone_name LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY p.paid_at DESC
ORDER BY p.paid_at ASC
ORDER BY p.payment_price DESC
ORDER BY p.payment_price ASC
ORDER BY p.paid_at DESC

mapper.update:
UPDATE payments
SET payment_status = #{paymentStatus}
WHERE reservation_id = #{reservationId}
