mapper.update:
UPDATE reservations
SET
refund_status = #{refundStatus},
refunded_at = #{refundedAt}
WHERE reservation_id = #{reservationId}

mapper.select:
SELECT r.reservation_id, u.nickname, z.zone_name AS campground_name, r.checkin_time, r.checkout_time, r.reservation_status, r.refund_status FROM reservations r JOIN users u ON r.provider_code = u.provider_code AND r.provider_user_id = u.provider_user_id JOIN campground_sites s ON r.reservation_site = s.site_id JOIN campground_zones z ON s.zone_id = z.zone_id
AND u.nickname LIKE CONCAT('%', #{name}, '%')
AND r.reservation_status = #{reservationStatus}
AND r.refund_status = #{refundStatus}
AND DATE(r.checkin_time) = #{checkinDate}
