mapper.update:
UPDATE users
SET user_role = #{role}
WHERE id = #{userId}

UPDATE users
SET user_status = #{status}
WHERE id = #{userId}

mapper.insert:
INSERT INTO users (
  provider_code, provider_user_id, email, nickname, profile_image,
  phone, user_role, point, checklist_alert, reservation_alert, vacancy_alert
)
VALUES (
  #{providerCode}, #{providerUserId}, #{email}, #{nickname}, #{profileImage},
  #{phone}, #{userRole}, #{point}, #{checklistAlert}, #{reservationAlert}, #{vacancyAlert}
)

mapper.select:
SELECT *
FROM users
WHERE provider_code = #{providerCode}
  AND provider_user_id = #{providerUserId}
